define('text!views/about/about.html',[],function () { return '<div id="about" class="container-fluid">\r\n    <div class="row-fluid">\r\n        <div class="span6">\r\n            <div class="about-content badge-strawberry">\r\n                <h1><i class="icon-user"></i>Who am I?</h1>\r\n                <p class="lead">My name is Yago and I am a Spanish developer and passionate about web design and programming</p>     \r\n            </div>                                                \r\n            <div class="about-content badge-strawberry" style="margin-top:-5px;">\r\n                <h1><i class="icon-eye-open"></i>What i like to do?</h1>\r\n                <p class="lead">I like to learn and explore new technologies and development environments . I also like to take a look at new and amazing projects to see how they evolve</p>                      \r\n            </div>                        \r\n        </div>\r\n        <div class="span6">\r\n            <div class="about-content badge-info">\r\n                <h1><i class="icon-comments"></i>How can you contact me?</h1>\r\n                <ul class="unstyled">\r\n                    <li><a href="https://github.com/yagopv" target="_blank"><i class="icon-github"></i>Github</a></li>\r\n                    <li><a href="https://twitter.com/yperezva" target="_blank"><i class="icon-twitter-sign"></i>Twitter</a></li>\r\n                    <li><a href="https://www.facebook.com/yago.perezvazquez" target="_blank"><i class="icon-facebook-sign"></i>Facebook</a>                        </li>           <li><a href="http://es.linkedin.com/pub/yago-p%C3%A9rez-v%C3%A1zquez/45/453/381" target="_blank"><i class="icon-linkedin-sign"></i>LinkedIn</a></li>\r\n                </ul>                                            \r\n            </div>        \r\n            <div class="about-content badge-info" style="margin-top:-5px;">\r\n                <h1><i class="icon-cloud"></i>Where I am?</h1>\r\n                <p class="lead">in a galaxy far, far away...</p>                \r\n            </div>                    \r\n        </div>            \r\n    </div>\r\n</div>';});

