define('text!views/notfound/main.html',[],function () { return '<div id="offcanvas-navigation">\r\n    <a href="#" class="showmenubutton btn btn-danger h-add-padding"><i class="icon-reorder icon-large no-ajaxy"></i></a>\r\n</div>\r\n\r\n<div>\t\r\n    <div class="hero-unit badge-important h-big-top-margin h-inverse-color">\r\n        <h1><i class="icon-exclamation-sign icon-large"></i>Page not found</h1>\r\n        <p class="lead h-big-top-margin">You have a wrong address and this site do not contains any page with this url</p>\r\n        <p class="h-big-top-margin">Try with any of the links provided in the sidebar to go and check Stashy components. You can always download or go to Github and check the project as well if is this what you are looking for</p>        \r\n        <a class="btn badge-danger-dark btn-large h-top-margin" href="https://github.com/yagopv/Stashy/archive/master.zip"  target="_blank"><i class="icon-download icon-2x"></i>&nbsp; Download</a>\t\t\t\t\t\t\t\r\n        <a class="btn badge-danger-dark btn-large h-top-margin" href="https://github.com/yagopv/Stashy"  target="_blank"><i class="icon-github icon-2x"></i>&nbsp; View project on Github</a>                                \r\n    </div>\r\n</div>';});

